#include <iostream>
#include <string>
#include <algorithm> // For std::max

class Calculator {
private:
    double result;

public:
    Calculator() : result(0.0) {}

    // Addition
    void add(double num) {
        result += num;
    }

    // Subtraction
    void subtract(double num) {
        result -= num;
    }

    // Multiplication
    void multiply(double num) {
        result *= num;
    }

    // Division
    void divide(double num) {
        if (num == 0) {
            std::cout << "Error: Division by zero!" << std::endl;
            return;
        }
        result /= num;
    }

    // Get current result
    double getResult() const {
        return result;
    }

};

int main() {
    Calculator calc;
    calc.add(10);
    calc.subtract(5);
    calc.multiply(2);
    calc.divide(2);
    calc.result = calc.getResult();
    std::cout << "Result: " << calc.getResult() << std::endl;

    return 0;
}
