#include <iostream>
#include <string>
#include <algorithm>

// Function to reverse a string using the STL
std::string reverseString(std::string str) {
  // 1. Create a copy of the string to avoid modifying the original.
  std::string reversed_str = str;

  // 2. Use the std::reverse algorithm to reverse the copied string.
  std::reverse(reversed_str.begin(), reversed_str.end());

  // 3. Return the reversed string.
  return reversed_str;
}

int main() {
  std::string myString = "hello";
  std::string reversedString = reverseString(myString);

  std::cout << "Original string: " << myString << std::endl;
  std::cout << "Reversed string: " << reversedString << std::endl;

  std::string anotherString = "world";
  std::string reversedAnotherString = reverseString(anotherString);
  std::cout << "Original string: " << anotherString << std::endl;
  std::cout << "Reversed string: " << reversedAnotherString << std::endl;

  return 0;
}
